INSERT INTO `yw_tract` VALUES (12, 241, '2019-07-15 00:00:00', '张凤双 郝楠 王立波', '半年度投放复盘汇报，客户觉得PPT只有数据的呈现，没有数据的对比，补充完再发给客户', '这半年服务质量和数据上是有提升的，但是我们没有表达好，建议以后重大的汇报公司可以出个汇报模版，再根据客户情况改动', '孙庆', '2019-07-22 10:34:56', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (13, 113, '2019-07-19 00:00:00', '贾博', '客户原生目前只有我们一家在投放，本次见面主要沟通暑期期间投放规划及预算，后续对接细节，推进付款进度', '近期原生只有我们一家投放，因齐欣原生账户内有剩余货款，所以本周会开启投放，客户进去不会让致维投放原生，客户付款会优先走我司付款流程', '李中会', '2019-07-22 10:40:44', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (14, 240, '2019-07-19 00:00:00', '刘娅', '账户现在量在降成本再升，原因是客户死守自己的人群包不愿意放开人群包，我们和直客都在做客户思想工作', '目前客户还没有被说通，还坚持自己的人群包，但是妥协会再提供一批人群包', '孙庆', '2019-07-22 10:40:52', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (15, 114, '2019-07-16 00:00:00', '王英姿，王媛', '与采购沟通神马框架合同审核事宜，目前框架合同在客户内部审核流程中，预计本月底前反馈审核结果，与业务部门沟通付款事宜，本周150万已付，其中50万已充值', '神马框架合同下周初稿反馈，150万应付款已到账，本周已约我司高层与律师见面沟通优信后续如果付款出问题怎么做防守', '李中会', '2019-07-22 10:46:27', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (16, 102, '2019-07-22 00:00:00', '唐行意', '光大银行8月会配合8.18活动投放一个月品专，目前三人行已确认投放，只做无线品专，本周安排下单。', '光大银行8月会配合8.18活动投放一个月品专，目前三人行已确认投放，只做无线品专，本周安排下单。', '唐行意', '2019-07-22 10:47:38', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (17, 112, '2019-07-22 00:00:00', '唐行意', '明一老板已批复百度投放预算，会签250万框，全年预算400万。客户会做品专max且同时实现精细化，希望价格不变。本周已安排询价，5折后267万，之前报给客户314万；另客户希望能提供3个月图片优化服务，成本在2万左右，成本完全含盖住。本周已开始走合同流程，并同步协调百度直客发起倒签框架特批，希望框架执行期间在2019.7.1-2020.6.30，本周与媒体直客反馈进度。', '明一老板已批复百度投放预算，会签250万框，全年预算400万。客户会做品专max且同时实现精细化，希望价格不变。本周已安排询价，5折后267万，之前报给客户314万；另客户希望能提供3个月图片优化服务，成本在2万左右，成本完全含盖住。本周已开始走合同流程，并同步协调百度直客发起倒签框架特批，希望框架执行期间在2019.7.1-2020.6.30，本周与媒体直客反馈进度。', '唐行意', '2019-07-22 10:48:06', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (18, 264, '2019-07-19 00:00:00', '施政', '旅行社这一块的投放业务拼框进携程的大框，客户内部在和其他部门PK利润分成，客户还希望再招一家代理商，两家代理商一起做这块业务', '下周市场部总经理还会谈一家代理商，网页模板已经开始设计，预计最快8月中旬可以下单；但是如果是两家的话，我们有可能会放弃', '朱静', '2019-07-22 10:48:59', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (19, 111, '2019-07-22 00:00:00', '唐行意', '此客户只投放搜索关键词和信息流，每月30万预算，不会持续投，全年预算120万左右；本周拜访客户沟通后续投放规划，9月份会有一波新的规划，预计50万，投放至年底。此客户基本只投百度媒体，主要是品牌露出，做给经销商看。推荐手机锁屏等资源，暂时都没有需求。', '此客户只投放搜索关键词和信息流，每月30万预算，不会持续投，全年预算120万左右；本周拜访客户沟通后续投放规划，9月份会有一波新的规划，预计50万，投放至年底。此客户基本只投百度媒体，主要是品牌露出，做给经销商看。推荐手机锁屏等资源，暂时都没有需求。', '唐行意', '2019-07-22 10:49:29', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (20, 125, '2019-07-17 00:00:00', '魏亚洲', '最近跌量明显，和客户沟通为什么会跌量，是受暑期房产淡季的影响吗', '客户反馈，后续由于客户内部项目调整的原因，房友圈的投放会暂停，什么时候再投放等通知。', '朱静', '2019-07-22 10:53:29', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (21, 126, '2019-07-17 00:00:00', '赵树鲜', '和客户沟通6月回款开票信息、6月消耗结算、回款时间，后续预算', '客户反馈6月的消耗，结算单已经给到财务了，在等财务通知她开票后，她通知我们开票，最晚付款时间是8月底，和当时客户说的差不多会比合同晚一个月时间一致，我会继续盯回款流程', '朱静', '2019-07-22 10:58:26', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (22, 115, '2019-07-16 00:00:00', '陈丹', '客户商务与投放只有陈丹一人对接，目前框架落后，7月下旬开始新车上市7-8月投放预算在120万，本周与客户见面沟通投放策略', '本周主要沟通4-6月投放结案报告与放量策略，本周结案报告与放量策略已发客户审核确认', '李中会', '2019-07-22 10:59:50', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (23, 267, '2019-07-19 00:00:00', '奶茶', '跟进测试投放及合同，合同已盖章归档，客户款项预付，测试期金额不大', '测试投放，抖音效果不好，成本较高，周五已跟客户沟通，投放头条试试看效果，还需继续优化', '何黎楠', '2019-07-22 11:03:57', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (24, 116, '2019-07-19 00:00:00', '郝燕超', '客户目前逾期款20万左右，本周主要与客户沟通付款进度与投放情况，目前客户框架消费进度较高，主要想推动客户升框', '目前客户逾期款付款流程在客户内部审核中，预计7月底前支付，客户目前不确定19年预算是否升框，8月份左右看客公司内部预算情况', '李中会', '2019-07-22 11:04:58', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (25, 269, '2019-07-22 00:00:00', '唐行意', '前期推荐娱乐大数据应用/电商大数据应用/小程序应用/手机锁屏/今日头条五部分的合作。计划下周约见市场经理徐岩。', '前期推荐娱乐大数据应用/电商大数据应用/小程序应用/手机锁屏/今日头条五部分的合作。计划下周约见市场经理徐岩。', '唐行意', '2019-07-22 11:07:02', '', NULL, NULL);
