INSERT INTO `yw_tract` VALUES (27, 168, '2019-07-17 00:00:00', '洪世伟', '客户要投放测试百度搜索与原生，目前客户正在准备物料，我司不走流程只做服务，服务费3%，目前合同审核中', '预计合同在7月底审核完，8月初开始投放，测试好会接着投放神马，搜狗，头条', '李中会', '2019-07-22 11:12:40', '', NULL, NULL);
