INSERT INTO `yw_tract` VALUES (33, 279, '2019-07-22 00:00:00', '冯骁', '客情沟通，约见拜访（凑字2345677888888888888888）', '约冯骁见面，沟通其他媒体投放机会。冯骁说我们高层约了周三拜访，聊锁屏系统', '马颖', '2019-07-22 15:21:03', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (34, 89, '2019-07-11 00:00:00', '王文涛', '智鸟方面的投放，会有一定提升，本周客户目前的佰嘉康+羽扇的框架已经补充完毕旅行社资质。可以开始变更框架返点，完成后将开始再追赶框架。', '客户所有旅游资质已经补充，本周开始进行框架返点变更，从在线旅游变为旅行社。目前客户配合程度高，本周开始，预计每日200万元回款会将之后的所有回款全都回款完毕', '孙依桄', '2019-07-22 15:32:05', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (35, 79, '2019-07-16 00:00:00', '孙雅瑜', '跟进客户其他媒体投放情况，客户预计投放搜狗，政策已报给客户，账期同百度媒体', '客户搜狗投放计划目前在内部审批流程中，目前还没有反馈，持续跟进中', '宋彤', '2019-07-22 16:25:43', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (36, 75, '2019-07-16 00:00:00', '王强', '跟进客户百度品专、搜索、原生信息流投放充值款项回款计划以及框架落后后续的投放计划', '目前客户8月前没有预算向我司进行回款，框架是否降框还尚未确定', '宋彤', '2019-07-22 16:28:52', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (37, 73, '2019-07-16 00:00:00', '李莹、雷雨芊', '跟进客户中标结果客户想同我司目前服务的客户：国美沟通电商信息流如何考核', '目前客户内部对招标及电商客户考核方式进行调研中，尚未进行回复，，告知客户因二代下单不便于提供联系方式', '宋彤', '2019-07-22 16:51:35', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (38, 198, '2019-07-17 00:00:00', '王欣', '沟通目前客户其他媒体投放情况，客户目前头条测试代理商已满，快手媒体也在测试', '快手先报政策后测试，目前我司快手资质尚未收到，待收到资质后确定吐点政策后同客户沟通快手测试机会，头条预计8月再开始新测试', '宋彤', '2019-07-22 16:54:44', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (39, 74, '2019-07-17 00:00:00', '王欣', '沟通目前客户其他媒体投放情况，客户目前快手媒体也在测试，头条目前测试代理商已满', '快手先报政策后测试，目前我司快手资质尚未收到，待收到资质后确定吐点政策后同客户沟通快手测试机会，头条预计8月再开始新测试', '宋彤', '2019-07-22 16:56:05', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (40, 187, '2019-07-09 00:00:00', '安雯', '本周同客户沟通后续投放预算，目前客户准备有新游戏上线，也在了解其他客户类似游戏情况', '目前客户市场问到的头条返点为5+X，2个月账期，我司有可能不能满足', '宋彤', '2019-07-22 16:57:42', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (41, 286, '2019-07-18 00:00:00', '付义', '电话联系客户约定拜访时间，以便于进一步沟通后续百度投放合作机会', '同客户约定本周三进行拜访，对于目前客户主体情况进一步沟通和后续的合作媒体', '宋彤', '2019-07-22 17:14:28', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (42, 76, '2019-07-15 00:00:00', '刘佳', '跟进客户19年框架下框进展，和18年所欠款项问题，后续各媒体的投放计划', '目前所有媒体投放均已停止，对于欠款问题，内部财务未批复，可进行催款邮件处理', '宋彤', '2019-07-22 17:16:42', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (43, 287, '2019-07-17 00:00:00', '高飞', '客户原8家代理进行投放，因客户内部需核算ROI，目前已缩减为4家代理', '目前客户缩减投放预算，待再加大预算投放时，增加代理投放，持续跟进', '宋彤', '2019-07-22 17:21:24', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (44, 195, '2019-07-17 00:00:00', '梁艺华', '客户账户近期没有展现、曝光，通过服务团队进行问题排查，并发给媒体邮件', '媒体反馈客户有超投问题，沟通客户账户超投问题，协助客户余额转账', '宋彤', '2019-07-22 17:27:15', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (45, 71, '2019-07-22 00:00:00', 'emma', '后半年投放广告， 主要针对，百度，小红书等资源。需要后续比稿', '目前客户新网站制作中，需要网站上线后再行投放，Emma会推荐我司参加比稿。', '刘海玲', '2019-07-22 18:35:42', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (46, 71, '2019-07-22 00:00:00', 'emma', '客户为蔻驰集团下新产品，之前只做线下投放无线上投放，但客户新作了网站，想要弥补此线上缺失投放，因此后半年广告需要投放，主要目标是百度，小红书。seo（客户有固定的供应商）', '客户网站没做完，需要网站完成后，我司加入供应商系统参与比稿，已经跟对接人沟通，发起推荐。', '刘海玲', '2019-07-22 18:39:39', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (47, 290, '2019-07-22 00:00:00', 'rego', '目前客户已将我司添加供应商系统，但是因为用的是海南公司，税务情况需要给客户提交，正在我司内部流程中', '合同客户已发，无问题可以签署合同内，另外客户测试预算10万，如果效果好可以追加预算。全年预算200万', '刘海玲', '2019-07-22 18:55:51', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (48, 65, '2019-07-22 00:00:00', '李爽', '客户分产品线国外品牌厨卫投放，想投放信息流，已经报价信息发给客户', '等待客户确认后， 发下单需求， 具体预算客户还在评估中', '刘海玲', '2019-07-22 18:58:11', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (49, 275, '2019-07-23 00:00:00', '王东亮', '客户在4月的时候已在凌众下框信息流100万，现在要把框架转移至我司，并且升框至1300万', '拟双方合作合同条款和媒介确认转框升框流程，保证金支付比例以及账期', '朱静', '2019-07-23 17:00:22', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (50, 123, '2019-07-23 00:00:00', '刘忠超', 'Q2的回款跟进，品众想拿走1000万的原生，和客户沟通以后，客户让品众的人和我直接沟通，客户说以我们的需求为首位', 'Q2可以按时回款；拒绝了品众要拿1000万原生的要求，等Q3再转移', '朱静', '2019-07-23 17:04:32', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (51, 119, '2019-07-23 00:00:00', '黄樨元', 'Q2集团季总回顾，广州想加大对原生信息流的投放，需要我司指导，另外广州还想测试LBS这个资源', '新建一个QQ群，集团、广州、我司，群内指导对产品的投放方法和策略，推进LBS资源售卖进展', '朱静', '2019-07-23 17:06:28', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (52, 121, '2019-07-23 00:00:00', '彭浩', '上周有一个跨区违规，和客户沟通后续投放上需要规避的风险点，给到客户建议', '上周有一个跨区违规，和客户沟通后续投放上需要规避的风险点，给到客户建议，客户在考虑是否结束这个框架，因为风险太大，现在我们在和渠道沟通保证金扣罚的比例', '朱静', '2019-07-23 17:07:44', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (53, 266, '2019-07-23 00:00:00', '冯佳、华楠', '商务点以及核减结算的问题，之前和运营聊客户要制定KPI，百度800元/授信，超过的部分按比例核减', '和瘦瘦聊了下我们的风险很大，授信的成本除了前端数据还取决于客户审核的通过率有关，而且客户要商务点7个点，所以我们决定先撑撑', '孙庆', '2019-07-23 18:32:40', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (54, 259, '2019-07-25 00:00:00', '常畅', '上次拜访客户说预计8月份我们可以入场测试，又和客户沟通确认了下是否可以8月进', '客户表示现在在测试的代理合同也要处理，测试不达标的代理会终止合作，让我们再等等，会第一时间通知我们', '孙庆', '2019-07-26 10:13:40', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (55, 266, '2019-07-25 00:00:00', '冯佳', '关于百度信息流政策相关的问题，客户要求核减或者交保证金，签框垫付保证金，商务点7个点', '已经给到客户回复，我们不接受核减、交保证金和垫付保证金，商务点和志维统一口径6个点货点，客户说要内部沟通下再回复我们', '孙庆', '2019-07-26 10:17:59', '', NULL, NULL);
