INSERT INTO `yw_tract` VALUES (76, 278, '2019-07-24 00:00:00', '百玲', '联系沟通 电话声音提示不在国内，无人接听.........', '电话无人接听，下周问下商务负责刘宇萌招标进展，因为之前沟通说开没有开始招标，所以定期跟进。下周约一下刘宇萌', '马颖', '2019-07-26 11:52:35', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (77, 49, '2019-07-26 00:00:00', '程涛', '抖音测试开启，预算50万，测试周期10天。跟进-内部商务流程/服务流程/开户流程', '1.内部商务流程备案完成-吐点4%，账期1个月。合同已发客户审核 2.走开户流程，商务资质准备，直客沟通对接（与直客联系告知我家开始测试，后续多关照） 3.内部服务对接。已找客户要到历史投放较好的素材，数据，以及优化方法，已经全部同步给优化团队，做投放前准备', '马颖', '2019-07-26 11:58:45', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (78, 87, '2019-07-25 00:00:00', '王文涛', '目前客户除了旅游行业，其他行业也有涉及，之后会尝试APP等行业是否可以进行下单。在保证框架安全的情况加会提升我司目前的消费情况', '客户有较大的预算投放阿拉丁，本周与客户同步约见百度渠道经理，就阿拉丁等情况与客户一块沟通，尽量开拓该客户多方面的产品下单情况。', '孙依桄', '2019-07-26 14:07:53', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (79, 84, '2019-07-26 00:00:00', '潘博', '乐居目前头条需求已经进行了初步沟通，本周客户会议较忙，预计下周1再对具体情况尽心沟通，并在下周内拜访客户，就客户目前的头条情况进行对接', '下周约见客户，与客户沟通头条的广告投放进展，另外客户目前百度系产品的投放体量近期有所下降，将我司的新产品与客户进行推介', '孙依桄', '2019-07-26 15:20:36', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (80, 438, '2019-07-26 00:00:00', '李珂', '与服务团队沟通是否接入服务，同步客户情况及KPI指标.。。。。。', '目前京东金融要找了4家服务商一起测试百度信息流，极易电商是其中一家，想把服务外部出来。给3%服务费，测试期15天，测试预算4万，KPI安卓激活成本15元以内，注册成本30以内。测试完成后4选1，后续预算350万/月。服务费3%的话，利润在10万/月。', '马颖', '2019-07-26 15:24:55', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (81, 51, '2019-07-26 00:00:00', '赵欣悦', '头条服务及返点情况...................................', '六间房开始测试头条，要求吐5个点，一个月账期，同时提供素材支持。跟客户沟通吐4个点，一个月账期，同时提供素材支持，可以接。目前已经从别家下单了，欣悦说等后面看看再说', '马颖', '2019-07-26 18:30:55', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (82, 72, '2019-07-24 00:00:00', '王佟夏', '拜访客户新办公地点，了解客户目前公司情况，跟进客户后续回款进度及计划', '目前客户公司员工200多人，新办公地点为优客工场，融资进度尚未确定，近期任务回款计划，客户公司员工已长时间拖欠', '宋彤', '2019-07-26 18:32:07', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (83, 286, '2019-07-25 00:00:00', '付义', '拜访客户，沟通资质问题，目前客户投放需求，目前投放情况，账期', '客户目前有百度品专投放需求，百度信息流在核桃林投放，头条也在投放，所有媒体投放消耗200万，目前客户商标证还在申请之中，客户目前投放代理均为2个月账期，需要服务优化', '宋彤', '2019-07-26 18:35:38', '', NULL, NULL);
