INSERT INTO `yw_tract` VALUES (96, 133, '2019-07-26 00:00:00', '邹毅', '与客户沟通后续账期事宜，最终谈拓的账期为按消耗结算，次次月15号前结算当月款；补充合同已跟客户确认，客户无异议，预计下周可归档；沟通头条框架终止事宜，客户已同意终止，已反馈给媒介并走合同，终止协议客户已确认', '今日头条补充协议已归档，快手信息流计划8月份开启，目前等待客户合同确认', '何黎楠', '2019-07-26 19:24:23', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (97, 129, '2019-07-26 00:00:00', '王树敏、夏睿', 'app下线，投放暂停；客户财务答复，近期公司资金紧张，分发的欠款需要拖延到8月初付，或者本月先付一半，周一还需要再和客户沟通，争取月底前回款；', 'app下线，投放暂停；客户财务答复，近期公司资金紧张，分发的欠款需要拖延到8月初付，或者本月先付一半，周一还需要再和客户沟通，争取月底前回款；', '何黎楠', '2019-07-26 19:24:58', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (98, 75, '2019-07-26 00:00:00', '王强', '跟进客户回款情况，约客户对目前欠款及后续回款计划沟通拜访时间', '下周一下午同客户CMO及投放负责人沟通欠款情况，准备目前欠款明细', '宋彤', '2019-07-26 19:26:11', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (99, 261, '2019-07-22 00:00:00', '东方', '本周多次和东方沟通头条投放成本问题，他非常注重服务回复的积极性，表示如果成本做不下来，但是服务好，也是加分的，已反馈给爱云，另百度品专报价已出，已和直客反馈了是否有折扣空间，直客下周一周会会特批申请，结果会后会和我同步，朋友圈还在沟通中', '成本那边一直在优化换素材，朋友圈还在跟进，品专价格已经出，下周会等直客是否可以批出折扣。', '刘先裕', '2019-07-26 20:24:56', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (100, 151, '2019-07-26 00:00:00', '王剑', '本周多次沟通客户的投放，其中武汉灵动因成本问题，搜索量有大幅下降，客户主要看成本', '武汉灵动因成本问题，搜索量有大幅下降，客户主要看成本来进行投放。', '刘先裕', '2019-07-26 20:27:14', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (101, 150, '2019-07-26 00:00:00', '周芳华', '直客本周已特批完成解绑无线单下PC，客户已开始准备无聊，预计8月1日投放，另客户因原生成本过高，已经让优化同学暂停账户投放，包括核桃林也是，具体再次上线时间未定', '客户因原生成本过高，已经让优化同学暂停账户投放，包括核桃林也是，具体再次上线时间未定，应该会等到搜索包重新更换完毕后。', '刘先裕', '2019-07-26 20:27:58', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (102, 153, '2019-07-24 00:00:00', '朱榜燕 曲雪娜', '本周朱榜燕那边拉了一个品牌后续对接SEM的人和我接触，我问了朱那边是不是可以理解就定了我们家，因为至今我还没提交报价等，朱那边说她已经不负责了品牌部了，具体也不清楚，但采购应该一共就接触了两家代理，一个新普，另一家是建材类的代理，我猜想应该是悦维，跟曲经理那边也沟通了，还是说让我等消息', '上周一直找曲经理，但一直未回复，本周曲经理回复我因她们一直内部培训，手机都没收了，今年和我司是有机会合作的，因公司流程较慢，所以只能在等一等', '刘先裕', '2019-07-26 20:30:00', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (103, 149, '2019-07-24 00:00:00', '许斌', '客户已经超出框架100%，预备让客户升级框架，客户那边一直考虑，因为要多付保证金等问题。', '本周并未有结果，，客户还未定，这两周的消耗正常，上周垫付的20万均已经回款。', '刘先裕', '2019-07-26 20:33:37', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (104, 143, '2019-07-25 00:00:00', '石伟 张伟健', '客户本周四有新游戏上线，客户反馈会根据成本有一些提高，但还是主要看成本，快手那边我也咨询了，他们近期包括头条都在控量，而且主要他们返点都要的特别高还要两个月的账期，所以我们也没往下细聊，另框架保证金已确认不需要我司支付', '框架保证金Apple已反馈特批完成，不用我司在出剩余保证金，提量问题还得看下周新游戏的成本情况。', '刘先裕', '2019-07-26 20:35:44', '', NULL, NULL);
