INSERT INTO `yw_tract` VALUES (118, 445, '2019-07-30 00:00:00', '包淳方', '推进今日头条、快手相关业务合作，目前把我司加入新增代理商中，我司报价今日头条3%、快手10%，包含运营', '客户先录入内部系统，等近期要新增代理商会首选我司加入，返点政策等等有待后续沟通', '童中保', '2019-07-31 15:19:05', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (119, 456, '2019-07-31 00:00:00', '李妍君', '推荐我司新出产品华为锁屏资源，推荐合作、了解客户2019年整体预算情况', '驴妈妈旅游网今年整体预算收紧，考核比较苛刻，所以先发资料了解下，周五进行客户回访', '童中保', '2019-07-31 15:21:11', '', NULL, NULL);
