INSERT INTO `yw_tract` VALUES (122, 435, '2019-08-01 00:00:00', '李小姐', '客户投放百度搜索与360搜索，客户要的返点与账期我们给不了，所以从菲索下单了', '客户投放百度搜索与360搜索，客户要的返点与账期我们给不了，所以从菲索下单了，后续跟进seo等项目推荐', '李中会', '2019-08-02 10:21:40', '', NULL, NULL);
