INSERT INTO `yw_tract` VALUES (143, 262, '2019-07-31 00:00:00', '马小明', '推进今日头条、百度开户事宜，了解客户以往媒体投放情况和近期投放计划等', '客户今日头条在中小目前还在和直客沟通中，希望能转移至KA投放。 百度媒体判定已出，客户投放得到8月底产品完善后投放', '童中保', '2019-08-02 18:59:30', '', NULL, NULL);
