INSERT INTO `yw_tract` VALUES (146, 92, '2019-08-02 00:00:00', '潘诗韵', '跟进随手记快手推广，目前随手记涉及P2P金融，暂受限投放，需添加下白名单。本周已跟快手渠道沟通准备相关资质。', '下周尽快安排添加白名单，后续随手记要走快手的招标流程', '唐行意', '2019-08-02 19:16:41', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (147, 442, '2019-08-02 00:00:00', '陈晨迪', '回答客户框架签署可能涉及的问题', '2019年7月起回款除保险业务外均由商务负责回款。客户准备更换投放主体，公司已更名为重庆度小满，已将更名所需资料发给晨迪，告知客户签约主体可以为北京度小满，广告主更名对账户投放无影响', '宋彤', '2019-08-03 21:01:44', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (148, 72, '2019-08-02 00:00:00', '张颖', '联系张颖，跟进客户后续内部沟通进展', '客户反馈将同邵总沟通，对已欠款项表示认可，但财务为了减少风险所以暂时对媒体扣罚不予确认，尽快反馈回款情况', '宋彤', '2019-08-03 21:07:55', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (149, 275, '2019-07-29 00:00:00', '王东亮', '框架转移和框架升框至1300万', '捕鱼框架，从凌众转移至我司，框架从100万升框至1300万，搜索和原生都是吐5%，合同已盖章存档，保证金客户缴纳', '朱静', '2019-08-04 16:14:05', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (150, 264, '2019-07-30 00:00:00', '施政、顺顺、张申琼', '1、朋友圈拼框；2、华为锁屏资源和安卓手机锁屏资源', '1、朋友圈拼框和华南二代在沟通，他那边在统计有多少量；2、和各部门沟通了锁屏资源，最后锁定了顺顺这个部门，已对接上，顺顺需要内部先沟通此资源的售卖形式', '朱静', '2019-08-04 16:29:34', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (151, 128, '2019-07-30 00:00:00', 'frank、金雅', '1、降框保证金的沟通；2、信息流GD词包的沟通', '1、降框保证金不扣罚这个特批直客还在内部审批中，会同步三方沟通；2、信息流GD本周又下单两个新词包，目前这个产品已下单224万', '朱静', '2019-08-04 16:33:32', '', NULL, NULL);
