INSERT INTO `yw_tract` VALUES (154, 123, '2019-07-31 00:00:00', '王敏', '6月消耗的回款和垫款额度的调整', '客户6月消耗提前回款，后续在原生的投放中还会加量，客户担心我司资金跟不上他们的要求，希望我司不要掉链子', '朱静', '2019-08-04 16:39:49', '', NULL, NULL);
