INSERT INTO `yw_tract` VALUES (277, 76, '2019-08-15 00:00:00', '李丽', '客户百度框架金额减少，代理报价及账期', '本周客户采购通知框架将降至250W（搜索210万，原生40万），要求进行报价返点、账期，我司按0返点、预付报价，并告知客户现在签署涉及不足年框架，已将报价、说明邮件反馈给客户采购', '宋彤', '2019-08-16 23:38:42', '', NULL, NULL);
