INSERT INTO `yw_tract` VALUES (296, 126, '2019-08-14 00:00:00', '赵树鲜', '6月消耗款的开票结算时间和快手的政策', '反馈6月的款会在8月底回给我们，由于最近在C轮中，所以财务端付款会有些慢，一旦付款闸口打开，会优先处理我们家；快手的政策是10个货，我这边先观察他们家的回款情况再考虑是否接入', '朱静', '2019-08-18 17:44:35', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (297, 123, '2019-08-13 00:00:00', '王敏、忠超', '信息流双擎的推荐和服务费合同的协商', '服务费合同条款已OK，账目日均消耗20万给1个点，日均消耗50万2个点，客户先盖章；客户需要我司制作小说情节视频素材投放，客户建议我司和他的其他媒体代理商一起合资拍摄，这样便于分摊拍摄成本，和客户沟通，由于目前没有利润，等后期有服务费了，我愿意拨出一部分去拍摄视频素材', '朱静', '2019-08-18 17:46:58', '', NULL, NULL);
