INSERT INTO `yw_tract` VALUES (362, 444, '2019-08-29 00:00:00', '阳薇', '拜访客户，沟通百度信息流投放情况', '同客户沟通目前投放情况，目前有三家代理在投放，目前我司投放量级占金50%，成本属于中上等水平，目前有代理已做到100左右成本，需要在创意素材上进行提升', '宋彤', '2019-08-30 16:46:11', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (363, 444, '2019-08-29 00:00:00', '郝雪', '沟通服务合同条款细节', '同客户沟通服务条款，因我司要更换签约主体-新疆普菲特，故需要提供变更主体声明、新疆普菲特相关资质，重新进行供应商入库，并且要求有同样百度核代资质', '宋彤', '2019-08-30 16:49:54', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (364, 442, '2019-08-29 00:00:00', '张杰', '沟通18年框架结算核算问题', '目前客户仍在核算18年框架充值、完成数据，对其中其他代理测试充值的金额同媒介沟通，并向客户解释当时充值情况为二代下单，我已积极配合客户进行数据核对，目前因为框架核对问题严重影响充值回款进度', '宋彤', '2019-08-30 16:52:37', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (365, 287, '2019-08-29 00:00:00', '高飞', '跟进客户合同流程进展', '目前客户已将合同提交法务审核中，政策也已确定，领导已经同意', '宋彤', '2019-08-30 16:54:22', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (366, 604, '2019-08-29 00:00:00', '刘洋', '沟通客户KA投放情况', '客户之前在分公司投放，目前有意向转KA进行投放，已向媒体发出能否KA开户需求', '宋彤', '2019-08-30 17:33:58', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (367, 606, '2019-08-30 00:00:00', '尚宇', '沟通客户目前各媒体投放情况', '沟通客户目前各媒体投放情况，目前客户在分公司进行投放，准备对接服务，后续其他媒体会有投放计划', '宋彤', '2019-08-30 17:52:04', '', NULL, NULL);
