INSERT INTO `yw_tract` VALUES (396, 606, '2019-09-05 00:00:00', '尚宇', '沟通服务细节及服务费收取比例，客户投放预算', '拜访客户，沟通服务细节及服务费收取比例，客户表示月均30-50W消耗，目前主要以百度、应用商店投放为主，后续将开其他媒体渠道', '宋彤', '2019-09-07 01:13:05', '', NULL, NULL);
