INSERT INTO `yw_tract` VALUES (399, 73, '2019-09-05 00:00:00', '王征', '月度汇报，贴吧品牌大使投放计划', '贴吧品牌大使资源报价已发客户，实收3个月10万，6个月15万，媒体并配送部分吧内资源，客户仍在讨论中 同客户沟通服务费考核维度，包含完成月度预算消耗，目前客户框架进度稳定，推动客户原生复投，主要因为百度媒体成本较高200+，明显高于其他媒体成本', '宋彤', '2019-09-07 01:16:53', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (400, 73, '2019-09-05 00:00:00', '于颖慧', '拜访客户沟通快手投放情况', '本周拜访客户沟通快手投放情况，另外一家代理为龙域，日均1000-2000，成本100以下；沟通投放素材还是需要符合人群调性', '宋彤', '2019-09-07 01:17:23', '', NULL, NULL);
