INSERT INTO `yw_tract` VALUES (404, 442, '2019-09-06 00:00:00', '张杰、陈晨迪', '沟通目前回款进度及框架结算进度', '沟通目前回款进度，目前逾期时间较长，框架结算客户业务端目前已核对完成，转移至财务，并且确认7月结算数据并可以开具发票；提供客户8月结算数据，客户已确认转移至商务；后续可能将会加快支付进度；从媒体得知18年框架优惠扣罚可能不扣罚，并且预计9月份完成框架签署', '宋彤', '2019-09-07 01:22:21', '', NULL, NULL);
