INSERT INTO `yw_tract` VALUES (419, 442, '2019-09-13 00:00:00', '刘晓晨', '沟通框架结算进度，回款进展', '客户发邮件准备将补充协议延期至11月30日，未提前告知具体情况原因，客户商务电话一直不能联系不上，还望领导可以出面协助沟通，具体情况', '宋彤', '2019-09-19 14:21:00', '', NULL, NULL);
