INSERT INTO `yw_tract` VALUES (421, 444, '2019-09-12 00:00:00', '郝雪', '跟进客户变更协议进度，后续投放进展', '客户在入库流程。客户目前已停止免费课程推广，所有媒体已停止投放，近期准备推广19元课程，目前等待客户落地页完成', '宋彤', '2019-09-19 14:23:18', '', NULL, NULL);
