INSERT INTO `yw_tract` VALUES (424, 604, '2019-09-20 00:00:00', '张萌', '同客户沟通中小转KA进度，客户十一后账户失效', '同客户沟通中小转KA进度，客户十一后账户失效，之后添加白名单，告知客户预计11月方可完成KA转移开户，并开始投放；目前客户投放缩小，主要是因为准备更换投放主体问题，转户完成后御尊行投放将停止', '宋彤', '2019-09-21 11:40:38', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (425, 442, '2019-09-18 00:00:00', '张杰、陈晨迪', '本周拜访客户进行双周汇报', '品牌账户消费下降归因，分析非品牌搜索词消费变化；原生百度小程序计划搭建，预算设置2000元，预计下周一、二开启测试，提前做好节前充值准备', '宋彤', '2019-09-21 11:44:11', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (426, 442, '2019-09-20 00:00:00', '刘晓晨、李永凤', '了解目前客户签署框架进度', '媒体同客户仍在就签署框架金额（1.25亿）进行协商，预计十一过后可以确定框架签署；同客户沟通延期合同至11月30日，并且要求我方对招标承诺5%搜索返点进行补返，同客户沟通将在9月结算时将之前7、8月的返点进行抵扣结算。8月结算目前已开票给客户，预计回款需要等到客户确定框架签署后才可陆续进行回款。', '宋彤', '2019-09-21 11:44:44', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (427, 198, '2019-09-19 00:00:00', '辛易娜、王欣', '同水滴保客户沟通近期有原生代运营需求', '同水滴保客户沟通近期有原生代运营需求，可以支付服务费，同服务团队沟通目前人员情况，缺少服务人员承接', '宋彤', '2019-09-21 11:45:42', '', NULL, NULL);
