INSERT INTO `yw_tract` VALUES (429, 80, '2019-09-19 00:00:00', '龙燕', '跟进回款进度，客户反馈目前投放情况', '客户反馈目前新素材上传缓慢，也没有老计划重新调整上传，ROI最低。同渠道沟通后续物料的修改方向。目前其他代理日均1W', '宋彤', '2019-09-21 11:47:02', '', NULL, NULL);
INSERT INTO `yw_tract` VALUES (430, 187, '2019-09-18 00:00:00', '安雯', '跟进客户投放计划及承接要求', '客户预计10月中旬开始新产品投放，目前在找头条代理商，可承接的需求分为代运营或者高返点，要求2个月账期，就目前服务人员吃紧情况，还没有回复客户我方政策', '宋彤', '2019-09-21 11:47:46', '', NULL, NULL);
